DeviceType.State:
uint8_t sendRight = 0;
uint8_t supSendRight = 0;
uint8_t sendLeft = 0;
uint8_t supSendLeft = 0;

DeviceType.ReadyToSend:
if (deviceState->sendRight == 1)
{
    *readyToSend |= RTS_FLAG_send_right;
}
if (deviceState->sendLeft == 1)
{
    *readyToSend |= RTS_FLAG_send_left;
}
if (deviceState->supSendRight == 1 || deviceState->supSendLeft == 1)
{
    *readyToSend |= RTS_SUPER_IMPLICIT_SEND_FLAG;
}

InputPin.OnReceive:
if (message->content == 47)
{
    if (deviceProperties->id == 2) 
    {
        deviceState->sendLeft = 1;
        deviceState->supSendLeft = 1;
    }
    else
    {
        deviceState->sendRight = 1;
        deviceState->supSendRight = 1;
    }
}

SupervisorOutPin.OnSend:
if (deviceState->supSendLeft == 1)
{
    message->id = deviceProperties->id;
    message->content = 208;
    deviceState->supSendLeft = 0;
}
else if (deviceState->supSendRight == 1)
{
    message->id = deviceProperties->id;
    message->content = 47;
    deviceState->supSendRight = 0;
}

SupervisorType.State:
uint8_t failed = 0;
uint8_t firstSent[3] = {0, 0, 0};
uint8_t secondSent[3] = {0, 0, 0};

SupervisorInPin.OnReceive:
/* Do nothing if we've failed already. */
if (supervisorState->failed == 0) {

FILE* resultFile = fopen("test_packet_chain_to_supervisor_result",
                         "w");

/* Check ID, failing fast. */
if (message->id > 2)
{
    fputs("1", resultFile);
    supervisorState->failed = 1;
}
else {
/* Check payload, failing fast. */
if (message->content != 47 and message->content != 208)
{fputs("1", resultFile); supervisorState->failed = 1;}
else {

/* Check whether we've already received the message in question,
 * failing fast. If not, set that we've received the message. */
if (message->content == 47)
{
    if (supervisorState->firstSent[message->id] == 1)
        {fputs("1", resultFile); supervisorState->failed = 1;}
    else supervisorState->firstSent[message->id] = 1;
}
else if (message->content == 208)
{
    if (supervisorState->secondSent[message->id] == 1)
        {fputs("1", resultFile); supervisorState->failed = 1;}
    else supervisorState->secondSent[message->id] = 1;
}

/* Check a failure condition. */
if (supervisorState->secondSent[0] == 1 or
    supervisorState->firstSent[2] == 1) {
    fputs("1", resultFile);
    supervisorState->failed = 1;
}

/* Check the pass condition. */
if (supervisorState->firstSent[0] == 1 and
    supervisorState->secondSent[0] == 0 and
    supervisorState->firstSent[1] == 1 and
    supervisorState->secondSent[1] == 1 and
    supervisorState->firstSent[2] == 0 and
    supervisorState->secondSent[2] == 1)
 {
     fputs("0", resultFile);
 }
 }}

 fclose(resultFile);

 }
